import {
    TextField,
    Button,
    withStyles,
    MenuItem,
    Typography,
    Tooltip,
} from '@material-ui/core';
import React, { useState, ChangeEvent, useContext, useEffect } from 'react';
import { UserContext } from '../../UserContext';
import './ActivityForm.css';
import axios from '../../Axios';
import GeoSuggest from '../MapComponents/GeoSuggest';
import Popup from '../Popup';
import MapComponent from '../MapComponents/MapComponent';
import { Marker } from 'react-google-maps';
import { Activity } from '../../interfaces/Activity';
import Equipment from '../../interfaces/Equipment';
import Tag from '../../interfaces/Tag';
import Pagination from '@material-ui/lab/Pagination';
import DefaultCenter from '../../interfaces/DefaultCenter';
import styled from 'styled-components';
import InfoIcon from '@material-ui/icons/Info';
import ActivityResponse from '../../interfaces/ActivityResponse';
import config from '../../Config'

const StyledButton = withStyles({
    root: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        borderRadius: 3,
        border: 0,
        color: 'white',
        height: 48,
        padding: '0 30px',
        boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        width: '15vw',
    },
    label: {
        textTransform: 'capitalize',
    },
})(Button);

const StyledTextField = withStyles({
    root: {
        minWidth: '30vh',
    },
})(TextField);

const NumberTextField = withStyles({
    root: {
        width: '7vw',
        padding: '5px',
    },
})(TextField);

const ButtonsContainer = styled.div`
    display: flex;
    width: 34vw;
    padding: 10px;
`;
interface Props {
    openPopup: boolean;
    setOpenPopup: React.Dispatch<React.SetStateAction<boolean>>;
    activityResponse?: ActivityResponse;
    groupId?: string | undefined;
}

const ActivityForm = ({
    openPopup,
    setOpenPopup,
    activityResponse,
    groupId,
}: Props) => {
    const [page, setPage] = useState<number>(1);
    const { user } = useContext(UserContext);
    const [title, setTitle] = useState('');
    const [desc, setDesc] = useState('');
    const [date, setDate] = useState<Date>(new Date());
    const [dateDisplay, setDateDisplay] = useState<string>('');
    const [location, setLocation] = useState<DefaultCenter>({
        lat: 50,
        lng: 50,
    });
    const [equipmentList, setEquipmentList] = useState<Equipment[]>([]);
    const [equipmentDesc, setEquipmentDesc] = useState('');
    const [counterAct, setCounterAct] = useState<number>(0);
    const [counterTag, setCounterTag] = useState<number>(0);
    const [reset, setReset] = useState<boolean>(false);
    const [openShowMap, setOpenShowMap] = useState<boolean>(false);
    const [tagList, setTagList] = useState<Tag[]>([]);
    const [tagsDesc, setTagsDesc] = useState<string>('');
    const [activityLevel, setActivityLevel] = useState<string>('');
    const [capacity, setCapacity] = useState<string>('');
    const [repetition, setRepetition] = useState<string>('');
    const [image, setImage] = useState<string>('');
    const [isActivityResponse, setIsActivityResponse] = useState<boolean>(
        false
    );

    const onPageChange = (event: React.ChangeEvent<unknown>, value: number) => {
        setPage(value);
    };

    const checkInput = (input: string) => {
        if (input.length > 0 && input.charAt(0) !== ' ') return true;
        else return false;
    };

    const onChangeTitle = (event: ChangeEvent<HTMLInputElement>) => {
        setTitle((event.target as HTMLInputElement).value);
    };

    const onChangeDesc = (event: ChangeEvent<HTMLInputElement>) => {
        setDesc((event.target as HTMLInputElement).value);
    };

    const onChangeDate = (event: ChangeEvent<HTMLInputElement>): void => {
        const strDate: string = (event.target as HTMLInputElement).value;
        console.log(strDate);
        console.log(new Date(strDate));
        setDateDisplay(strDate);
        setDate(new Date(strDate));
    };

    const onChangeEqipmentDesc = (
        event: ChangeEvent<HTMLInputElement>
    ): void => {
        setEquipmentDesc((event.target as HTMLInputElement).value);
    };

    const onChangeTagsDesc = (event: ChangeEvent<HTMLInputElement>) => {
        const str: string = (event.target as HTMLInputElement).value;
        setTagsDesc(str);
    };

    const onChangeActivityLevel = (event: ChangeEvent<HTMLInputElement>) => {
        const str: string = (event.target as HTMLInputElement).value;
        setActivityLevel(str);
    };

    const onChangeCapacity = (event: ChangeEvent<HTMLInputElement>) => {
        const str: string = (event.target as HTMLInputElement).value;
        if (+str < 1) {
            setCapacity('');
        } else {
            setCapacity(str);
        }
    };

    const onChangeRepetitions = (event: ChangeEvent<HTMLInputElement>) => {
        const str: string = (event.target as HTMLInputElement).value;
        if (+str < 1) {
            setRepetition('');
        } else if (+str > 3) {
            setRepetition('');
        } else {
            setRepetition(str);
        }
    };

    const onChangeImage = async (
        event: React.ChangeEvent<HTMLInputElement>
    ) => {
        console.log(event);
        if (event.target.files != null) {
            const file: File = event.target.files[0];
            console.log(file);
            const base64 = await convertBase64(file);
            console.log(base64);
            setImage(base64);
            console.log(image);
        }
    };

    const convertBase64 = (file: File) => {
        return new Promise<any>((resolve, reject) => {
            const fileReader = new FileReader();
            fileReader.readAsDataURL(file);

            fileReader.onload = () => {
                resolve(fileReader.result);
            };
            fileReader.onerror = (error) => {
                reject(error);
            };
        });
    };

    const addEquipment = (event: React.KeyboardEvent): boolean => {
        if (event.key === 'Enter') {
            if (checkInput(equipmentDesc)) {
                const equipment: Equipment = {
                    equipmentId: counterAct,
                    description: equipmentDesc,
                };
                equipmentList.push(equipment);
                setCounterAct(counterAct + 1);
                setEquipmentDesc('');
                setReset(false);
                return true;
            } else {
                console.log('Could not add equipment. Not a valid description');
                setEquipmentDesc('');
            }
        }
        return false;
    };

    const deleteEquipment = (ID: number) => {
        setEquipmentList(
            equipmentList.filter((equip) => equip.equipmentId !== ID)
        );
    };

    const addTags = (event: React.KeyboardEvent) => {
        if (event.key == 'Enter') {
            if (checkInput(tagsDesc)) {
                const tags: string[] = tagsDesc.split(',');
                tags.map((tag) => {
                    tagList.push({ tagId: counterTag, description: tag });
                });
                setCounterTag(counterTag + 1);
                setTagsDesc('');
                setReset(false);
            }
        }
    };

    const deleteTag = (ID: number) => {
        setTagList(tagList.filter((tag) => tag.tagId !== ID));
    };

    const isDisabled = (): boolean => {
        if (title === '' || date === new Date() || activityLevel === '') {
            return true;
        } else {
            return false;
        }
    };

    const getEquipmentString = () => {
        let equipmentString = '';
        equipmentList.map(
            (equipment) => (equipmentString += equipment.description + ',')
        );
        return equipmentString;
    };
    const getTagString = () => {
        let tagString = '';
        tagList.map((tag) => (tagString += tag.description + ','));
        return tagString;
    };

    const getTimeFormat = (): string => {
        return (
            date.getFullYear() +
            '-' +
            (+date.getMonth() + 1) +
            '-' +
            date.getDate() +
            ' ' +
            (+date.getHours() + 2) +
            ':' +
            date.getMinutes() +
            ':' +
            date.getSeconds()
        );
    };

    const escapedJSONDescription = () => {
        return JSON.stringify(desc)
            .replace(/\\n/g, '\\n')
            .replace(/\\'/g, "\\'")
            .replace(/\\"/g, '\\"')
            .replace(/\\&/g, '\\&')
            .replace(/\\r/g, '\\r')
            .replace(/\\t/g, '\\t')
            .replace(/\\b/g, '\\b')
            .replace(/\\f/g, '\\f');
    };

    const postActivity = (groupId: string | undefined) => {
        const id: number = groupId ? +groupId : -1;
        let capa = 1;
        let repe = 0;
        try {
            capa = capacity !== '' ? +capacity : 1;
            repe = repetition !== '' ? +repetition : 0;
        } catch (error) {
            console.log('Could not convert string to number');
        }
        const activity: Activity = {
            title: title,
            time: getTimeFormat(),
            repeat: repe,
            userId: user,
            capacity: capa,
            groupId: id,
            description: escapedJSONDescription(),
            image: image,
            activityLevel: activityLevel.toUpperCase(),
            equipmentList: getEquipmentString(),
            tags: getTagString(),
            latitude: location.lat,
            longitude: location.lng,
        };
        console.log(activity);

        axios
            .post('/activity', activity, config)
            .then((response) => {
                JSON.stringify(response);
                console.log(response.data);
            })
            .then(() => {
                handleReset();
                setOpenPopup(!openPopup);
            })
            .catch((error) =>
                console.log(error.response.data)
            );
    };

    const onKeyDown = (e: any) => {
        if (e.code === 'ArrowRight' && page < 8) {
            setPage(page + 1);
        }
        if (e.code === 'ArrowLeft' && page > 1) {
            setPage(page - 1);
        }
    };

    const changeActivity = async () => {
        console.log(activityLevel)
        const sendActivity = {
            title: title,
            time: getTimeFormat(),
            repeat: repetition,
            userId: user,
            capacity: capacity,
            groupId: 0,
            description: escapedJSONDescription(),
            image: image,
            activityLevel: activityLevel.toUpperCase(),
            equipmentList: getEquipmentString(),
            tags: getTagString(),
            latitude: location.lat,
            longitude: location.lng,
        };
        console.log(sendActivity);
        const token = localStorage.getItem('token');
        const config = {
            headers: {
                token: token,
            },
        };
        const request = await axios
            .put(
                `/activity/${activityResponse?.activityId}`,
                sendActivity,
                config
            )
            .then(() => {
                handleReset();
                setOpenPopup(!openPopup);
            });
        console.log(request);
    };

    const handleReset = () => {
        setTitle('');
        setDesc('');
        setDate(new Date());
        setDateDisplay('');
        setEquipmentList([]);
        setEquipmentDesc('');
        setCounterAct(0);
        setCounterTag(0);
        setReset(true);
        setOpenShowMap(false);
        setTagList([]);
        setTagsDesc('');
        setActivityLevel('');
        setCapacity('');
        setRepetition('');
    };

    function getPosition(string: string, subString: string, index: number) {
        return string.split(subString, index).join(subString).length;
    }

    function getDisplayDate(date: Date): string {
        return date.toJSON().substring(0, getPosition(date.toJSON(), ':', 2));
    }
    useEffect(() => {
        if (activityResponse !== undefined) {
            setIsActivityResponse(true);
            console.log(activityResponse);
            setTitle(activityResponse.title);
            setLocation({
                lat: activityResponse.latitude,
                lng: activityResponse.longitude,
            });
            const date: Date = new Date(activityResponse.time);
            setDate(date);
            setDateDisplay(getDisplayDate(date));

            setDesc(activityResponse.description);
            setImage(activityResponse.image);
            let i = 0;
            activityResponse.tags.map((tag) => {
                tagList.push({ tagId: i, description: tag });
                i++;
            });
            setEquipmentList(activityResponse.equipments);
            setActivityLevel(activityResponse.activityLevel);
            setCapacity(activityResponse.capacity + '');
            setRepetition(activityResponse.daysToRepeat + '');
        }
    }, []);

    return (
        <div className="activityform" onKeyDown={onKeyDown}>
            {page === 1 && (
                <div>
                    <Typography
                        className="activityform__containerItem1"
                        component="h2"
                    >
                        Legg til tittel*
                    </Typography>
                    <StyledTextField
                        label="Tittel"
                        value={title}
                        onChange={onChangeTitle}
                        variant="outlined"
                    />
                </div>
            )}
            {page === 2 && (
                <div>
                    <Typography component="h2">Legg til plassering*</Typography>
                    <div className="activityform__place">
                        <div className="activityform__placeItem1">
                            <GeoSuggest
                                onLocationChange={(location) => {
                                    setLocation(location);
                                }}
                            />
                        </div>
                        <StyledButton
                            className="activityform__placeButton"
                            onClick={() => setOpenShowMap(!openShowMap)}
                        >
                            Vis på kart
                        </StyledButton>
                        <Popup
                            openPopup={openShowMap}
                            setOpenPopup={setOpenShowMap}
                            maxWidth="lg"
                        >
                            <MapComponent
                                width="75vh"
                                height="75vh"
                                defaultCenter={location}
                            >
                                <Marker position={location}></Marker>
                            </MapComponent>
                        </Popup>
                    </div>
                </div>
            )}
            {page === 3 && (
                <div>
                    <Typography
                        className="activityform__containerItem1"
                        component="h2"
                    >
                        Legg til dato
                    </Typography>
                    <StyledTextField
                        label="Dato"
                        type="datetime-local"
                        defaultValue={dateDisplay}
                        value={dateDisplay}
                        InputLabelProps={{
                            shrink: true,
                        }}
                        onChange={onChangeDate}
                        variant="outlined"
                    />
                </div>
            )}
            {page === 4 && (
                <div>
                    <Typography
                        className="activityform__containerItem1"
                        component="h2"
                    >
                        Legg til beskrivelse
                    </Typography>
                    <StyledTextField
                        className="activityform__description"
                        label="Beskrivelse"
                        value={desc}
                        onChange={onChangeDesc}
                        variant="outlined"
                        rows={4}
                        multiline
                    />
                </div>
            )}
            {page === 5 && (
                <div>
                    <Typography
                        className="activityform__containerItem1"
                        component="h2"
                    >
                        Legg til bilde
                    </Typography>
                    <StyledTextField
                        type="file"
                        label="Bilde"
                        onChange={onChangeImage}
                        InputLabelProps={{
                            shrink: true,
                        }}
                        variant="outlined"
                    />
                    <img src={image} style={{ maxHeight: '40px' }} />
                </div>
            )}
            {page === 6 && (
                <div>
                    <div className="activityform__header">
                        <Typography
                            className="activityform__headerItem1"
                            component="h2"
                        >
                            Legg til tags
                        </Typography>
                        <Tooltip
                            placement="right-start"
                            title="Press enter for å legge til. Trykk for å fjerne"
                        >
                            <InfoIcon />
                        </Tooltip>
                    </div>
                    <StyledTextField
                        label="Tags"
                        onChange={onChangeTagsDesc}
                        onKeyPress={addTags}
                        value={tagsDesc}
                        variant="outlined"
                    />
                    <ul className="activityform__list">
                        {reset === false &&
                            tagList.map((tag) => (
                                <li
                                    key={tag.tagId}
                                    onClick={deleteTag.bind(this, tag.tagId)}
                                >
                                    {tag.description}
                                </li>
                            ))}
                    </ul>
                </div>
            )}
            {page === 7 && (
                <div>
                    <div className="activityform__header">
                        <Typography
                            className="activityform__headerItem1"
                            component="h2"
                        >
                            Legg til utstyr
                        </Typography>
                        <Tooltip
                            placement="right-start"
                            title="Press enter for å legge til. Trykk for å fjerne"
                        >
                            <InfoIcon />
                        </Tooltip>
                    </div>
                    <StyledTextField
                        label="Utstyr"
                        value={equipmentDesc}
                        variant="outlined"
                        onChange={onChangeEqipmentDesc}
                        onKeyPress={addEquipment}
                    />
                    <ul className="activityform__list">
                        {reset === false &&
                            equipmentList.map((equipment) => (
                                <li
                                    key={equipment.equipmentId}
                                    onClick={deleteEquipment.bind(
                                        this,
                                        equipment.equipmentId
                                    )}
                                >
                                    {equipment.description}
                                </li>
                            ))}
                    </ul>
                </div>
            )}
            {page === 8 && (
                <div>
                    <div className="activityform__header">
                        <Typography className="activityform__headerItem1">
                            Legg til aktivitetsgrad*, plasser og gjentakninger
                        </Typography>
                        <Tooltip
                            placement="left-end"
                            title="Plasser må være minst 1. Gjentakninger vil opprette en tilsvarende aktivitet 1 uke frem tid. Gjentakninger må være mellom 0 og 3."
                        >
                            <InfoIcon />
                        </Tooltip>
                    </div>
                    <div className="acitivityform__containerItem2">
                        <TextField
                            className="activityform__activityLevel"
                            label="Aktivitetgrad"
                            onChange={onChangeActivityLevel}
                            value={activityLevel}
                            variant="outlined"
                            select
                        >
                            <MenuItem value="High">High</MenuItem>
                            <MenuItem value="Medium">Medium</MenuItem>
                            <MenuItem value="Low">Low</MenuItem>
                        </TextField>
                        <div className="acitivityform__containerItem22">
                            <NumberTextField
                                className="acitivityform__numberbox"
                                label="Plasser"
                                type="number"
                                InputLabelProps={{
                                    shrink: true,
                                }}
                                value={capacity}
                                onChange={onChangeCapacity}
                                variant="outlined"
                            />
                            {!activityResponse &&
                                <NumberTextField
                                    className="acitivityform__numberbox"
                                    label="Gjentakinger"
                                    type="number"
                                    InputLabelProps={{
                                        shrink: true,
                                    }}
                                    variant="outlined"
                                    value={repetition}
                                    onChange={onChangeRepetitions}
                                />
                            }
                        </div>
                    </div>
                    <ButtonsContainer>
                        <StyledButton
                            className="activityform__add"
                            onClick={
                                isActivityResponse
                                    ? changeActivity
                                    : () => postActivity(groupId)
                            }
                            disabled={isDisabled()}
                        >
                            {isActivityResponse ? 'Endre ' : 'Legg til'}
                        </StyledButton>
                        <StyledButton
                            className="activityform__reset"
                            onClick={handleReset}
                        >
                            Reset
                        </StyledButton>
                    </ButtonsContainer>
                </div>
            )}
            <div className="activityform__pageContainer">
                <Pagination page={page} onChange={onPageChange} count={8} size="large" />
            </div>
        </div>
    );
};

export default ActivityForm;
